mapper.select:
	select
  	
  	from eshop_user
  	where 1 = 1
  	
  		AND username like #{username}
  	
  	
  		AND realname like #{realName}
  	
  	
  		AND valid = #{valid}
  	
  	order by add_time desc,upd_time desc

	select
  	count(*)
  	from eshop_user
  	
  		AND username like #{username}
  	
  	
  		AND realname like #{realName}
  	
  	
  		AND valid = #{valid}
  	
  	order by add_time desc,upd_time desc
